class Button extends Component {

    constructor(props) {
        super(props)

        this.state = {
            graphType: "barGraph"
        }
    }

    clickHander() {
        this.setState({
            graphType: "lineGtaph"
        })
    }
    render() {
        return (
        <div>
            <div>{this.state.graphType}</div>
        <button>onClick={this.clickHandler}>Click!</button>
        </div>
    )
}
}
export default Button;
