import React from 'react';

import { Cards, Chart, CountryPicker } from './components';
import styles from './App.module.css';
import { fetchData } from './api';
import covid19Image from './images/covid19_image2.png';
// import Button from './components/Button'; 
// import {GraphPicker} from './components/GraphPicker.jsx';

// import MapDisplay from './components/MapDisplay/MapDisplay.js';

class App extends React.Component {

    state = {
        data: {},
        country: '',
        graphType: '',
    };

    // with componentDidMount, the async goes before it instead of after like usual functions
    async componentDidMount() {
        const fetchedData = await fetchData(); // fetchData is an asyncronous function so it needs an await
        console.log(fetchedData);
        this.setState({ data: fetchedData});
    }

    handleCountryChange = async (country) => {
        // get the data
        const fetchedData = await fetchData(country);
        console.log(fetchedData);
        // set the state
        this.setState({ data: fetchedData, country: country});
    }
    handleGraphPicker(selectedGraph) {
        // this.setState({ graphType: this.state.graphType + selectedGraph });
        // alert('Clicked!');
        console.log("This is the selected graph passed in: " + selectedGraph);
        this.state.graphType = selectedGraph;
        console.log("!!!!!!!!!!!!!!!!!!The selected graph changed to: " + this.graphType);
    }

    render() {
        const { data, country } = this.state;
        return (
            <div className={styles.container}>
                <img className={styles.image} src={covid19Image} alt="COVID-19"/>
                <Cards data = {data} />
                <CountryPicker handleCountryChange={this.handleCountryChange} />
                <button onClick={this.handleGraphPicker('lineGraph')}>Line Graph</button>
                <button onClick={this.handleGraphPicker('barGraph')}>Bar Graph</button>
                <Chart data = {data} country = {country} />
                {/* <h1>Here goes the map</h1> */}
            </div>
        )
    }
}

export default App;
