import React, {useState, useEffect} from 'react';
import { fetchDailyData } from '../../api';
import { Line, Bar } from 'react-chartjs-2';

import styles from './Chart.module.css';

const Chart = ({ data, country, graphType}) => {
    const [dailyData, setDailyData] = useState([]);
    console.log("***This is the graph type passed into Chart.jsx = ", graphType)
    console.log("***This is the country type passed into Chart.jsx = ", graphType)
    useEffect(() => {
        const fetchAPI = async () => {
            setDailyData(await fetchDailyData());
        }
        // console.log(initialDailyData);
        fetchAPI();
    }, []); // this will only happen once 

    const lineChart = (
        dailyData[0] ? (
            <Line 
                data={{
                    labels: dailyData.map(({ date }) => date),
                    datasets: 
                    [{
                        data: dailyData.map((data) => data.confirmed),
                        label: 'Infected',
                        borderColor: '#3333ff',
                        fill: true,
                    }, 
                    {
                        data: dailyData.map((data) => data.recovered),
                        label: 'Recovered',
                        borderColor: 'green',
                        fill: true,
                    }, 
                    {
                        data: dailyData.map((data) => data.deaths),
                        label: 'Deaths',
                        borderColor: 'red',
                        backgroundColor: 'rgba(255, 0, 0, 0.5',
                        fill: true,
                    },
                    ],
                }}
            />
            ) : null
    );

    console.log("Line - This is in Chart.js and this is data for infected: " + dailyData.map((data) => data.confirmed));
    console.log("Line - This is in Chart.js and this is data for recovered: " + dailyData.map((data) => data.recovered));
    console.log("Line - This is in Chart.js and this is data for deaths: " + dailyData.map((data) => data.deaths));


    // const lineChart = (
    //     data.confirmed ? (
    //         <Line 
    //             data={{
    //                 labels: dailyData.map(({ date }) => date),
    //                 datasets: 
    //                 [{
    //                     label: 'Infected',
    //                     borderColor: 'blue',
    //                     fill: true,
    //                     data: [data.infected.value]
    //                 },
    //                 {
    //                     label: 'Recovered',
    //                     data: [data.recovered.value],
    //                     borderColor: 'green',
    //                     fill: true,
    //                 },
    //                 {
    //                     label: 'Deaths',
    //                     data: [data.deaths.value],
    //                     borderColor: 'red',
    //                     fill: true,
    //                 },
    //             ],
    //             }}
    //         />
    //     ) : null
    // );

    const barChart = (
        data.confirmed ? (
            <Bar 
                data={{
                    labels: ['Infected', 'Recovered', 'Deaths'],
                    datasets: 
                    [{
                        label: 'People',
                        backgroundColor: 
                        [
                            'rgba(0, 0, 255, 0.5', // infected = blue
                            'rgba(0, 255, 0, 0.5', // recovered = green
                            'rgba(255, 0, 0, 0.5', // deaths = red
                        ], 
                        data: [data.confirmed.value, data.recovered.value, data.deaths.value]
                    }]
                }}
                options=
                {{
                    legend: { display: false},
                    title: { display: true, text:`Current state in ${country}`}
                }}
            />
        ) : null
    );

    // if there is a country chosen, then we display a bar chart for that country
    // otherwise display the line chart for the global values
    return (
        <div className={styles.container}>
            {/* {country ? barChart : lineChart}  */}
            {barChart}
            {/* {lineChart} */}
        </div>
    )
}

export default Chart;
